Cod($_SESSION['usuarioCod']);                if(!empty($acaoModuloIdPermissao)){            if(!$this->permissaoAcao($acaoModuloIdPermissao)){                throw new \Exception('Sem permissão');            }        }    }    /**     * Ao informar a acão, o metodo verifica se o usuário tem acesso a ela (ação),     * este método captura automaticamente o usuarioCod da sessão e nome do      * módulo que deve ser definido como variavel constante     * @param string $acaoModuloIdPermissao     * @return bool     */    public function permissaoAcao($acaoModuloIdPermissao)    {        return (bool) $this->con->execNLinhas($this->acessoSql->permissaoAcao(parent::getModuloNome(), $acaoModuloIdPermissao, parent::getUsuarioCod()));    }    /**     * Retorna os dados de um módulo, o módulo em questão estaria definido previamente     * como uma constante     * @return array     */    public function dadosModulo()    {        return $this->con->execLinha($this->acessoSql->dadosModulo(parent::getModuloNome()));    }    /**     * Retorna os dados de uma ação de um módulo,     * este método captura automaticamente o usuarioCod da sessão e nome do      * módulo que deve ser definido como variavel constante     * @param string $acaoModuloIdPermissao     * @return array     */    public function dadosAcaoModulo($acaoModuloIdPermissao)    {        return $this->con->execLinha($this->acessoSql->dadosAcaoModulo(parent::getModuloNome, $acaoModuloIdPermissao));    }    /**     * Retorna as opções de módulo que o usuário tem direito     * @return array     */    public function permissoesModulo()    {        return $this->con->paraArray($this->acessoSql->permissoesModulo(parent::getModuloNome(), parent::getUsuarioCod()),null, 'acaomoduloidpermissao');    }}