<?php/***   @author Pablo Vanni - pablovanni@gmail.com*   @since 23/02/2005*   Última Atualização: 14/10/2014*   Autualizada Por: Pablo Vanni - pablovanni@gmail.com*   @name Verifica permissões e fornece informações de acesso sobre moduloNomes*/namespace Zion\Acesso;abstract class AcessoVO{    private $usuarioCod;     private $moduloNome;    private $acaoModuloIdPermissao;      public function setUsuarioCod($valor)    {        $this->usuarioCod = $valor;        return $this;    }    public function getUsuarioCod()    {        return $this->usuarioCod;    }    public function setModuloNome($valor)    {        $this->moduloNome = $valor;        return $this;    }    public function getModuloNome()    {        return $this->moduloNome;    }    public function setAcaoModuloIdPermissao($valor)    {        $this->acaoModuloIdPermissao = $valor;        return $this;    }    public function getAcaoModuloIdPermissao()    {        return $this->acaoModuloIdPermissao;    }}