<?class AcessoGS{    /**    *	Atributos da Classe    */    private $UsuarioCod, $Modulo, $Opcao, $Acao, $Mobile;        /**    *	Seta o Código do Usuário    */    public function setUsuarioCod($Valor)    {        $this->UsuarioCod = $Valor;    }    /**    *	Recupera o Nome do Usuário    */    public function getUsuarioCod()    {        return $this->UsuarioCod;    }    /**    *	Seta o Módulo requisitado para acesso    */    public function setModulo($Valor)    {        $this->Modulo = $Valor;    }    /**    *	Recupera Módulo requisitado para acesso    */    public function getModulo()    {        return $this->Modulo;    }    /**    *	Seta a Opção requisitada    */    public function setOpcao($Valor)    {        $this->Opcao = $Valor;    }    /**    *	Recupera a Opção requisitada    */    public function getOpcao()    {        return $this->Opcao;    }    /**    *	Seta a Ação solicitada    */    public function setAcao($Valor)    {        $this->Acao = $Valor;    }    /**    *	Recupera a Ação solicitada    */    public function getAcao()    {        return $this->Acao;    }    public function setMobile($Valor)    {        return $this->Mobile = $Valor;    }    public function getMobile()    {        return $this->Mobile;    }}