<?include_once($_SESSION['FMBase'].'tabelas.class.php');class Filtro{    public function montaFiltro($ArrayCampos)    {        $Html      = "";        $Hiddens   = "";        //<span id="sis_filtrar_filtro"><img src="'.$_SESSION['UrlBase'].'figuras/mostrar_filtros.gif" border="0" onClick="sisFiltrarFiltro(\''.MODULO.'\')" /></span><span id="sis_salvar_filtro"><img src="'.$_SESSION['UrlBase'].'figuras/bt_salvar_filtro.gif" border="0" onClick="sisCadastrarFiltro(\''.MODULO.'\')" /></span>        $StrFormI  = '<div id="sis_form_filtros"><img id="imgSisFiltro" src="'.$_SESSION['UrlBase'].'figuras/sis_filtro_mostrar.gif" onClick="sisShowFiltro()"></div>';        $StrFormI .= '<form action="" method="get" name="FormFiltro" id="FormFiltro" class="forms" onSubmit="return false" style="display:none">';        $StrFormF  = '</form>';        if(is_array($ArrayCampos))        {            $Tab = new Tabelas();            $Html  = $Tab->tabIni();            //Cria Linhas            foreach ($ArrayCampos as $Valor)            {                $Cont += 1;                if($Valor[0] == true)                {                    if(!empty($Valor[1]))                    $Linha[$Valor[3]] .= '<td class="itensFiltro" align="right" nowrap="nowrap">'.$Valor[1].$Tab->fechaTd();                    $Linha[$Valor[3]] .= '<td class="itensFiltro" nowrap="nowrap">'.$Valor[2].$Tab->fechaTd();                }                else                {                    $Escondido[] = $Valor[2];                }            }            //Descarrega Campos Escondidos            if(is_array($Escondido)) foreach ($Escondido as $Campos) $Hiddens .= $Campos;            //Cria estrutura de Filtro            foreach ($Linha as $Conteudo)            {                $Html .= $Tab->abreTr();                $Html .= $Conteudo;                $Html .= $Hiddens.$Tab->fechaTr();            }            $Html .= $Tab->tabFim();            //Hidden de Intercepção a paginação            $Html .= '<input name="PaginaAtual"   id="SisPaginaAtual"   type="text" value="" size="1" style="display:none" />';            $Html .= '<input name="QuemOrdena"    id="SisQuemOrdena"    type="text" value="" size="1" style="display:none" />';            $Html .= '<input name="TipoOrdenacao" id="SisTipoOrdenacao" type="text" value="" size="1" style="display:none" />';            return $StrFormI.$Html.$StrFormF;        }        return $Html;    }}?>