if($Submeter) $Campo .= " onchange=\"submeter()\"";				$Campo .= " $Adicional $Estilo >\n";						if($Inicio) $Campo .= "<option value=\"\">Selecione...</option>\n";				foreach($Vetor as $Chave => $Vale)		{				$Campo .= "<option value=\"".$Chave."\" "; 						if($Chave == $Valor) $Campo .= "Selected";						$Campo .= " > \n".$Vale." </option> \n";		} 				return $Campo .= "</select>\n";	}		}?>