<?php/** *   @author Pablo Vanni - pablovanni@gmail.com *   @since 18/11/2005 *   Última Atualização: 13/10/2014 *   Autualizada Por: Pablo Vanni - pablovanni@gmail.com *   @name Cria uma grid de resultados com paginação */namespace Zion\Grid;abstract class Grid extends GridVO{    /**     * 	@param MeusDadosConverte Array - Objeto do tipo ObjConverte     * 	usado para passar uma funcao     *   ao setCondicaoTodosResultados     */    private $meusDadosConverte;    public function __construct()    {        parent::__construct();    }    /**     * 	Cria um link de ordenação     * 	@param Valor String - Identificação do Titulo para ordenação     * 	@param NomePagina String - Nome da Pagina que deve ser chamada     * 	@param CampoBd String - Campo da Tabela a Ser ordenado     * 	@param Parametros String - Parametros do Filtro         * 	@return String     */    public function ordena($valor, $campoTb)    {        $quemOrdena = parent::getQuemOrdena();        $metodoFiltra = parent::getMetodoFiltra();        $paginaAtual = parent::getPaginaAtual();        $naoOrdenePor = parent::getNaoOrdenePor();        //Iniciar ordenando Ascendente         $tipoOrdenacao = (empty(filter_input(INPUT_GET, 'to'))) ? 'ASC' : parent::getTipoOrdenacao();        //Verifica Se o Não Permite Ordenação        if (in_array($campoTb, $naoOrdenePor)) {            return sprintf('<span>%s</span>', $valor);        }        if ($campoTb == $quemOrdena) {            if ($tipoOrdenacao == "DESC") {                $novoTipo = "ASC";                $img = '<img src="figuras/desc.gif" border="0">';            } else {                $novoTipo = "DESC";                $img = '<img src="figuras/desc.gif" border="0">';            }            //Seta Quem ordena            \Zion\Paginacao\Parametros::setParametros("Full", ["qo" => $campoTb, "pa" => $paginaAtual]);            //Muda o Tipo de Ordenação do Link            $qS = \Zion\Paginacao\Parametros::addQueryString(\Zion\Paginacao\Parametros::getQueryString(), ["to" => $novoTipo]);        } else {                        $img = '';                        //Seta quem ordena e o tipo de ordenacao            \Zion\Paginacao\Parametros::setParametros("Full", ["qo" => $campoTb, "to" => $tipoOrdenacao, "pa" => $paginaAtual]);            //Recupera QS            $qS = \Zion\Paginacao\Parametros::getQueryString();        }        //Ordenação        if (!empty($novoTipo)){            $tipoOrdenacao = $novoTipo;        }                //Imagem de Ordenação        $iOr = '<img src="figuras/ordem.gif" border="0">';        return '<a href="javascript:sis_svo("' . $campoTb . '"," '. $tipoOrdenacao . '"); ' . $metodoFiltra . '("' . $qS . '");" >' . $iOr . $valor . $img . '</a>';    }    public function converteValor($linha, $dadosConverte)    {        $getDadosConverte = (is_array($dadosConverte) ? $dadosConverte : $this->meusDadosConverte);        $objeto = $dadosConverte[0];//Esta sendo exucutada pelo eval        $metodo = $dadosConverte[1];        $campo = $dadosConverte[2];        $pI = (empty($getDadosConverte[3])) ? array() : $getDadosConverte[3];        $pE = (empty($getDadosConverte[4])) ? array() : $getDadosConverte[4];        $ordem = $dadosConverte[5];        if (!empty($pI)){            foreach ($pI as $valor){                $arrayPI[] = $linha[$valor];            }        }                if ($ordem == "IE") {            $arParametros = (empty($arrayPI)) ? $pE : array_merge($arrayPI, $pE);        } else {            $arParametros = (empty($arrayPI)) ? $pE : array_merge($pE, $arrayPI);        }        if (!is_array($arParametros)) {            return $linha[$campo];        } else {            foreach ($arParametros as $valores){                $parametros .= "'" . $valores . "',";            }                        $parametros = substr($parametros, 0, -1);            eval("\$retorno = \$objeto->$metodo($parametros);");            return $retorno;        }    }    public function processaTotalizador($config, $itens)    {        $fPHP = new \Zion\Validacao\Numero();        if (!empty($config) and ! empty($itens) and is_array($itens)) {            $resultado = 0;            switch ($config['tipo']) {                case 'conta':                    $resultado = count($itens);                    break;                case 'soma':                    $itens = $this->traduzTotalizador($itens);                    $resultado = array_sum($itens);                    break;                case 'media':                    $itens = $this->traduzTotalizador($itens);                    $resultado = round(array_sum($itens) / count($itens), 2);                    break;                default :$resultado = " - ";            }            switch ($config['mascara']) {                case "moeda":                    return $config['prefixo'] . " " . $fPHP->floatCliente($resultado) . " " . $config['sufixo'];                default:                    return $config['prefixo'] . " " . $resultado . " " . $config['sufixo'];            }        }        return "-";    }    private function traduzTotalizador($itens)    {        $fPHP = new \Zion\Validacao\Numero();                foreach ($itens as $chave => $valor) {            $traduzido = $fPHP->floatBanco(implode(preg_split('/[^[:digit:] (,.)]/', $valor)));            $itens[$chave] = $traduzido;        }        return $itens;    }        public function resultadoEval($linha, $evalCod)    {        eval($evalCod[0]);        return $r;    }    public function inForm($valor, $nomeForm)    {        $iniForm = '<form id="' . $nomeForm . '" name="' . $nomeForm . '" method="post" class="forms" onSubmit="return false">';        $fimForm = '</form>';        return $iniForm . $valor . $fimForm;    }    public function setMeusDadosConverte($dadosConverte)    {        $this->meusDadosConverte = $dadosConverte;    }}