<?namespace Zion\Html;class Html{    public function abreTagAberta($tag, array $atributos, array $complemento = array())    {        $attr = vsprintf(str_repeat('"%s"', count($atributos)), $atributos);        $comp = vsprintf(str_repeat('%s', count($complemento)), $complemento);        return '<' . $tag . ' ' . $attr . $comp . '>';    }    public function abreTagFechada($tag, array $atributos, array $complemento = array())    {        $attr = vsprintf(str_repeat('"%s"', count($atributos)), $atributos);        $comp = vsprintf(str_repeat('%s', count($complemento)), $complemento);        return '<' . $tag . ' ' . $attr . $comp . '>';    }    public function fechaTag($tag)    {        return '</' . $tag . '>';    }}