<?php/** * @author Pablo Vanni - pablovanni@gmail.com * @since 01/06/2006 * Última Atualização: 28/05/2007 * Última Atualização: 16/03/2010 * Última Atualização: 10/02/2011 Passou a Trabalhar com Vetores - Corrigidos Erros de Acesso - Muitooo Mais Rápida * Atualizada por: Pablo Vanni - pablovanni@gmail.com * Última Atualização: 08/10/2014 Passou a gerar toda a estrura do menu em uma matriz hierárquica de niveis infinitos. Muito mais abstrata. * Atualizada por: Feliphe, o Retaliador - feliphezion@gmail.com * @name Gera uma estrutura de menus para acesso aos módulos do sistema, considerando as permissões do usuário logado. * Última Atualização: 09/10/2014 Adequado o retorno json e outras paradas mainstream. Muito mais tocha! * Corrigida por: Pozzebon, the Black Horse - vpozzebon@gmail.com * @version 3.0 * @package Framework */namespace Zion\Menu;class Menu extends MenuSql{    private $urlBase;    private $arrayGrupo;    private $arrayModulo;    private $arrayPermissao;    private $gruposSelecionados;    private $modulosSemReferencia;    private $modulosComReferencia;    private $modulosReferentes;    private $arrayPacote;    public function __construct()    {        parent::__construct();        $this->urlBase = SIS_URL_BASE;        $this->arrayGrupo = array();        $this->arrayModulo = array();        $this->arrayPermissao = array();        $this->gruposSelecionados = array();        $this->modulosSemReferencia = array();        $this->modulosComReferencia = array();        $this->modulosReferentes = array();        $this->arrayPacote = array();    }    /**     * Menu::geraMenu()     * Monta um JSON com a estrutura de menus, de acordo com as permissões atribuídas ao usuário logado.     *      * @return string JSON com a estrura encontrada. Array ([1] => Array ([Grupo] => Grupo Maior [ModulosGrupo] => Modulos e Submodulos do grupo))     * @example Sempre que um array dentro de ModulosGrupo tiver um outro array na chave Subs, este será um subgrupo podendo ter módulos e outros subgrupos, recursivamente. False otherwise.     */    public function geraMenu()    {        if (!isset($_SESSION['usuarioCod'])) {            return json_encode(array("sucesso" => false, "retorno" => "Usuário não autenticado."));        }        $usuarioCod = $_SESSION['usuarioCod'];        $this->arrayGrupo = $this->con->paraArray(parent::gruposDiponiveisSql());        $this->arrayModulo = $this->con->paraArray(parent::modulosDiponiveisSql(), null, "moduloCod");        $this->arrayPermissao = $this->con->paraArray(parent::usuarioPermissaoModuloSql($usuarioCod), "moduloCod");        $this->processaDetalhes();        $json = array();        foreach ($this->arrayGrupo as $dadosGrupo) {            if (!in_array($dadosGrupo['grupoCod'], $this->gruposSelecionados)) {                continue;            }            $this->arrayPacote[$dadosGrupo['grupoCod']] = $dadosGrupo['grupoPacote'];            $grupoModuloCod = $dadosGrupo['grupoCod'];            $json[$grupoModuloCod]['grupo'] = $dadosGrupo['grupoNome']; //Inicia um grupo.            $json[$grupoModuloCod]['grupoClass'] = $dadosGrupo['grupoClass']; //Inicia um grupo.            $json[$grupoModuloCod]['modulosGrupo'] = $this->geraModulos($dadosGrupo['grupoCod']); //Carrega os módulos e subgrupos do grupo recém-iniciado.        }        return json_encode(array('sucesso' => true, 'retorno' => $json));    }    /**     * Menu::processaDetalhes()     *      * @return     */    private function processaDetalhes()    {        $todosModulos = array();        foreach ($this->arrayModulo as $dadosModulo) {            $todosModulos[$dadosModulo['moduloCod']] = $dadosModulo['moduloCod'];        }        $this->processaPermissoes($todosModulos);        foreach ($this->arrayModulo as $dadosModulo) {            if (in_array($dadosModulo['moduloCod'], $this->arrayPermissao)) {                $this->gruposSelecionados[$dadosModulo['grupoCod']] = $dadosModulo['grupoCod'];            }            if (empty($dadosModulo['moduloCodReferente'])) {                $this->modulosSemReferencia[$dadosModulo['grupoCod']][$dadosModulo['moduloCod']] = $dadosModulo['moduloCod'];            } else {                $this->modulosComReferencia[$dadosModulo['moduloCod']] = $dadosModulo['moduloCodReferente'];                $this->modulosReferentes[$dadosModulo['moduloCodReferente']][] = $dadosModulo['moduloCod'];            }        }    }    /**     * Menu::processaPermissoes()     *      * @param mixed $todosModulos     * @return     */    private function processaPermissoes($todosModulos)    {        foreach ($todosModulos as $moduloCod) {            if (!empty($this->arrayModulo[$moduloCod]['moduloCodReferente']) and in_array($moduloCod, $this->arrayPermissao)) {                $this->analisaPermissaoRecursivo($moduloCod);            }        }        $this->arrayPermissao = array_unique($this->arrayPermissao);    }    /**     * Menu::analisaPermissaoRecursivo()     *      * @param mixed $moduloCod     * @param mixed $buffer     * @return     */    private function analisaPermissaoRecursivo($moduloCod, $buffer = array())    {        $buffer[$moduloCod] = $moduloCod;        if (empty($this->arrayModulo[$moduloCod]['moduloCodReferente'])) {            $this->arrayPermissao = array_merge($this->arrayPermissao, $buffer);        } else {            $this->analisaPermissaoRecursivo($this->arrayModulo[$moduloCod]['moduloCodReferente'], $buffer);        }    }    /**     * Menu::geraModulos()     *      * @param mixed $grupoCod     * @return     */    private function geraModulos($grupoCod)    {        $nModulos = count($this->modulosSemReferencia[$grupoCod]);        //Inicia Módulo        $json = array();        if ($nModulos > 0) {            foreach ($this->modulosSemReferencia[$grupoCod] as $moduloCod) {                if (in_array($moduloCod, $this->arrayPermissao)) {                    array_push($json, $this->geraModulo($moduloCod));                }            }        }        return $json;    }    /**     * Menu::geraModulo()     *      * @param mixed $moduloCod     * @return     */    private function geraModulo($moduloCod)    {        //Se módulo Possui Sub Modulo        if (in_array($moduloCod, $this->modulosComReferencia)) {            return $this->geraSubModulo($moduloCod);        }        $dadosModulo = $this->arrayModulo[$moduloCod];        if (in_array($moduloCod, $this->arrayPermissao) and $dadosModulo['moduloVisivelMenu'] == 'S') {            return $this->populaModulo($dadosModulo['moduloNome'], $dadosModulo['moduloNome'], $dadosModulo['moduloNomeMenu'], $this->arrayPacote[$dadosModulo['grupoCod']], $dadosModulo['moduloBase'], $dadosModulo['moduloClass']);        }    }    /**     * Menu::geraSubModulo()     *      * @param mixed $moduloCod     * @return     */    private function geraSubModulo($moduloCod)    {        $dadosSubModulo = $this->arrayModulo[$moduloCod];        $nModulos = count($this->modulosReferentes[$moduloCod]);        $json = array();        if ($nModulos > 0 and in_array($moduloCod, $this->arrayPermissao)) {            $json = $this->iniciaSubModulo($dadosSubModulo['moduloNome'], $dadosSubModulo['moduloNome'], $dadosSubModulo['moduloNomeMenu'], $dadosSubModulo['moduloClass']);            foreach ($this->modulosReferentes[$moduloCod] as $referenciaCod) {                if (in_array($referenciaCod, $this->modulosComReferencia)) {                    $json['subs'][$referenciaCod] = $this->geraModulo($referenciaCod);                } else {                    $json['subs'][$referenciaCod] = $this->geraModulo($referenciaCod);                }            }            return $json;        }    }    /**     * MenuCss::iniciaSubModulo()     *      * @param mixed $moduloDesc     * @param mixed $nomeMenu     * @return     */    private function iniciaSubModulo($moduloNome, $moduloDesc, $nomeMenu, $moduloClass = '')    {        return array("modulo" => $moduloNome, "menu" => $nomeMenu, "menuDesc" => $moduloDesc, "menuUrl" => "#", "moduloClass" => $moduloClass);    }    /**     * MenuCss::populaModulo()     *      * @param mixed $moduloNome     * @param mixed $moduloDesc     * @param mixed $nomeMenu     * @param mixed $pacote     * @param mixed $moduloBase     * @return     */    private function populaModulo($moduloNome, $moduloDesc, $nomeMenu, $pacote, $moduloBase, $moduloClass = '')    {        if ($moduloBase == 'S') {            $Base = SIS_URL_BASE;        } else {            $Base = SIS_URL_BASE;        }        $Url = $this->urlBase . $pacote . "/" . $moduloNome . "/";        return array("modulo" => $moduloNome, "menu" => $nomeMenu, "menuDesc" => $moduloDesc, "menuUrl" => $Url, "moduloClass" => $moduloClass, "subs" => false);    }}