<?/** * @author Pablo Vanni - pablovanni@gmail.com * @since 23/02/2005 * Autualizada Por: Pablo Vanni - pablovanni@gmail.com<br> * @name  Paginação de resultado para uma consulta no banco de dados * @version 2.0 * @package Framework */namespace Zion\Paginacao;abstract class PaginacaoVO{    /**     * 	Atributos da Classe     */    private $tipoOrdenacao = ""; //String  - Indica o tipo de ordenação ASC ou DESC    private $quemOrdena = ""; //String  - Indica um atributo especifico para ser ordenado    private $sql = ""; //String  - Sql    private $tabelaMestra = ""; //String  - Tabela que deve ser extraida no numero de registros    private $sqlContador = ""; //String  - Sql responsável por contar número de registros da Grid    private $filtroAtivo = false; //Booleano - Deve indicar se existe ou não ocoorencia de filtro    private $limitAtivo = true; //Indica se o SELECT deve receber LIMIT's de Paginação    private $chave = ""; //String  - Identificação da Chave Identificadora    private $metodoFiltra = ""; //String  - Informa a Grid Qual Método ela deve chamar para executar o filtro    private $qLinhas = 0; //Inteiro  - Informa o Número Maximo de Resultados por página    private $paginaAtual = 1; //Inteiro  - Informa o número da pagina atual - Este numero é varivael de acordo com o numero de linhas    private $irParaPagina = true; //Booleano - Ir diretamente para a página desejada | Habilitar ou não esta opção na paginação    private $alterarLinhas = true; //Booleano - Mostrar opção para alterar o numero de linhas(resultados) de uma grid    public function setTipoOrdenacao($Valor)    {        $this->tipoOrdenacao = $Valor;    }    public function getTipoOrdenacao()    {        $Order = strtoupper($this->tipoOrdenacao);        return ($Order == "ASC") ? "ASC" : "DESC";    }    public function setQuemOrdena($Valor)    {        $this->quemOrdena = $Valor;    }    public function getQuemOrdena()    {        return $this->quemOrdena;    }    public function setSql($Valor)    {        $this->sql = $Valor;    }    public function getSql()    {        return $this->sql;    }    public function setTabelaMestra($Valor)    {        $this->tabelaMestra = $Valor;    }    public function getTabelaMestra()    {        return $this->tabelaMestra;    }    public function setSqlContador($Valor)    {        $this->sqlContador = $Valor;    }    public function getSqlContador()    {        return $this->sqlContador;    }    public function setFiltroAtivo($Valor)    {        $this->filtroAtivo = $Valor;    }    public function getFiltroAtivo()    {        return $this->filtroAtivo;    }    public function setLimitAtivo($Valor)    {        $this->limitAtivo = $Valor;    }    public function getLimitAtivo()    {        return $this->limitAtivo ? true : false;    }    public function setChave($Valor)    {        $this->chave = $Valor;    }    public function getChave()    {        return $this->chave;    }    public function setMetodoFiltra($Valor)    {        $this->metodoFiltra = $Valor;    }    public function getMetodoFiltra()    {        return (empty($this->metodoFiltra)) ? "sis_filtrar" : $this->metodoFiltra;    }    public function setQLinhas($Valor)    {        $this->qLinhas = $Valor;    }    public function getQLinhas()    {        return(is_numeric($this->qLinhas)) ? $this->qLinhas : 0;    }    public function setPaginaAtual($Valor)    {        $this->paginaAtual = $Valor;    }    public function getPaginaAtual()    {        return (empty($this->paginaAtual)) ? 1 : $this->paginaAtual;    }    public function setIrParaPagina($Valor)    {        $this->irParaPagina = (bool) $Valor;    }    public function getIrParaPagina()    {        return $this->irParaPagina;    }    public function setAlterarLinhas($Valor)    {        $this->alterarLinhas = (bool) $Valor;    }    public function getAlterarLinhas()    {        return $this->alterarLinhas;    }    public function addVariaveisPaginacao(Array $Variaveis, $metodoEnvio = 'GET')    {        Parametros::setParametros($metodoEnvio, $Variaveis);    }}