<?php/** * @author Pablo Vanni - pablovanni@gmail.com * @since 23/02/2005 * Autualizada Por: Pablo Vanni - pablovanni@gmail.com<br> * @name  Paginação de resultado para uma consulta no banco de dados * @version 2.0 * @package Framework */namespace Zion\Paginacao;abstract class PaginacaoVO{    private $tipoOrdenacao; //String  - Indica o tipo de ordenação ASC ou DESC    private $quemOrdena; //String  - Indica um atributo especifico para ser ordenado    private $sql; //String  - Sql    private $tabelaMestra; //String  - Tabela que deve ser extraida no numero de registros    private $sqlContador; //String  - Sql responsável por contar número de registros da Grid    private $filtroAtivo; //Booleano - Deve indicar se existe ou não ocoorencia de filtro    private $limitAtivo; //Indica se o SELECT deve receber LIMIT's de Paginação    private $chave; //String  - Identificação da Chave Identificadora    private $metodoFiltra; //String  - Informa a Grid Qual Método ela deve chamar para executar o filtro    private $qLinhas; //Inteiro  - Informa o Número Maximo de Resultados por página    private $paginaAtual; //Inteiro  - Informa o número da pagina atual - Este numero é varivael de acordo com o numero de linhas    private $irParaPagina; //Booleano - Ir diretamente para a página desejada | Habilitar ou não esta opção na paginação    private $alterarLinhas; //Booleano - Mostrar opção para alterar o numero de linhas(resultados) de uma grid    /**     * PaginacaoVO::__construct()     *      * @return     */    public function __construct()    {        $this->filtroAtivo = false;        $this->limitAtivo = true;        $this->qLinhas = 0;        $this->paginaAtual = 1;        $this->irParaPagina = true;        $this->alterarLinhas = true;    }    /**     * PaginacaoVO::setTipoOrdenacao()     *      * @return     */    public function setTipoOrdenacao($valor)    {        $this->tipoOrdenacao = $valor;    }    /**     * PaginacaoVO::getTipoOrdenacao()     *      * @return     */    public function getTipoOrdenacao()    {        $Order = strtoupper($this->tipoOrdenacao);        return ($Order == "ASC") ? "ASC" : "DESC";    }    /**     * PaginacaoVO::setQuemOrdena()     *      * @return     */    public function setQuemOrdena($valor)    {        $this->quemOrdena = $valor;    }    /**     * PaginacaoVO::getQuemOrdena()     *      * @return     */    public function getQuemOrdena()    {        return $this->quemOrdena;    }    /**     * PaginacaoVO::setSql()     *      * @return     */    public function setSql($valor)    {        $this->sql = $valor;    }    /**     * PaginacaoVO::getSql()     *      * @return     */    public function getSql()    {        return $this->sql;    }    /**     * PaginacaoVO::setTabelaMestra()     *      * @return     */    public function setTabelaMestra($valor)    {        $this->tabelaMestra = $valor;    }    /**     * PaginacaoVO::getTabelaMestra()     *      * @return     */    public function getTabelaMestra()    {        return $this->tabelaMestra;    }    /**     * PaginacaoVO::setSqlContador()     *      * @return     */    public function setSqlContador($valor)    {        $this->sqlContador = $valor;    }    /**     * PaginacaoVO::getSqlContador()     *      * @return     */    public function getSqlContador()    {        return $this->sqlContador;    }    /**     * PaginacaoVO::setFiltroAtivo()     *      * @return     */    public function setFiltroAtivo($valor)    {        $this->filtroAtivo = $valor;    }    /**     * PaginacaoVO::getFiltroAtivo()     *      * @return     */    public function getFiltroAtivo()    {        return $this->filtroAtivo;    }    /**     * PaginacaoVO::setLimitAtivo()     *      * @return     */    public function setLimitAtivo($valor)    {        $this->limitAtivo = $valor;    }    /**     * PaginacaoVO::getLimitAtivo()     *      * @return     */    public function getLimitAtivo()    {        return $this->limitAtivo ? true : false;    }    /**     * PaginacaoVO::setChave()     *      * @return     */    public function setChave($valor)    {        $this->chave = $valor;    }    /**     * PaginacaoVO::getChave()     *      * @return     */    public function getChave()    {        return $this->chave;    }    /**     * PaginacaoVO::setMetodoFiltra()     *      * @return     */    public function setMetodoFiltra($valor)    {        $this->metodoFiltra = $valor;    }    /**     * PaginacaoVO::getMetodoFiltra()     *      * @return     */    public function getMetodoFiltra()    {        return (empty($this->metodoFiltra)) ? "sisFiltrar" : $this->metodoFiltra;    }    /**     * PaginacaoVO::setQLinhas()     *      * @return     */    public function setQLinhas($valor)    {        $this->qLinhas = $valor;    }    /**     * PaginacaoVO::getQLinhas()     *      * @return     */    public function getQLinhas()    {        return(is_numeric($this->qLinhas)) ? $this->qLinhas : 0;    }    /**     * PaginacaoVO::setPaginaAtual()     *      * @return     */    public function setPaginaAtual($valor)    {        $this->paginaAtual = $valor;    }    /**     * PaginacaoVO::getPaginaAtual()     *      * @return     */    public function getPaginaAtual()    {        return (empty($this->paginaAtual)) ? 1 : $this->paginaAtual;    }    /**     * PaginacaoVO::setIrParaPagina()     *      * @return     */    public function setIrParaPagina($valor)    {        $this->irParaPagina = (bool) $valor;    }    /**     * PaginacaoVO::getIrParaPagina()     *      * @return     */    public function getIrParaPagina()    {        return $this->irParaPagina;    }    /**     * PaginacaoVO::setAlterarLinhas()     *      * @return     */    public function setAlterarLinhas($valor)    {        $this->alterarLinhas = (bool) $valor;    }    /**     * PaginacaoVO::getAlterarLinhas()     *      * @return     */    public function getAlterarLinhas()    {        return $this->alterarLinhas;    }    /**     * PaginacaoVO::addVariaveisPaginacao()     *      * @return     */    public function addVariaveisPaginacao(Array $variaveis, $metodoEnvio = 'GET')    {        Parametros::setParametros($metodoEnvio, $variaveis);    }}