<?namespace Zion\Paginacao;class Parametros{    public static $Parametros = array();    public static function limpaParametros()    {        self::$Parametros = array();    }    public static function setParametros($metodo, $valores)    {        switch (strtoupper($metodo)) {            case "GET":                foreach ($valores as $valor) {                    $getValor = filter_input(INPUT_GET, $valor);                    if ($getValor != '') {                        self::$Parametros[$valor] = $getValor;                    }                }                break;            case "POST":                foreach ($valores as $valor) {                    $postValor = filter_input(INPUT_POST, $valor);                    if ($postValor != '') {                        self::$Parametros[$valor] = $postValor;                    }                }                break;            case "REQUEST":                foreach ($valores as $valor) {                    $requestValor = filter_input(INPUT_REQUEST, $valor);                    if ($requestValor != '') {                        self::$Parametros[$valor] = $requestValor;                    }                }                break;            case "FULL":                foreach ($valores as $valor => $conteudo) {                    self::$Parametros[$valor] = $conteudo;                }                break;        }    }    public static function getQueryString()    {        if (is_array(self::$Parametros) and ! empty(self::$Parametros)) {                        $query = '';                        foreach (self::$Parametros as $campo => $valor) {                $query .= "&" . $campo . "=" . urlencode($valor);            }            return substr($query, 1);        } else {            return "";        }    }    public static function addQueryString($queryAtual, $campoValor)    {        if (!empty($queryAtual)) {            $vCampos = explode("&", $queryAtual);            if (is_array($vCampos)) {                foreach ($vCampos as $campoJunto) {                    $vetJunto = explode("=", $campoJunto);                    $arrayRetorno[$vetJunto[0]] = $vetJunto[1];                }                foreach ($campoValor as $campo => $valor) {                    $arrayRetorno[$campo] = $valor;                }                foreach ($arrayRetorno as $campo => $valor) {                    $novaQuery.="&" . $campo . "=" . $valor;                }                return substr($novaQuery, 1);            }        } else {            foreach ($campoValor as $campo => $valor) {                $novaQuery.= "&" . $campo . "=" . $valor;            }            return substr($novaQuery, 1);        }    }}