<?php/** * @author Pablo Vanni - pablovanni@gmail.com */class Variaveis{    /**     * 	Limpando caracteres em branco do inicio e fim da string     * 	@param Var String - Variavel a ser manipulada     * 	@return String     */    public function limpa($var)    {        return trim($var);    }    /**     * 	Tratando caracteres especiais da string     * 	@param Var String - Variavel a ser manipulada     * 	@return String     */    public function converteHTML($var)    {        return htmlspecialchars($var, ENT_QUOTES);    }    public function trataAspas($var)    {        if (get_magic_quotes_gpc()) {            return $var;        } else {            return addslashes($var);        }    }    /**     * 	Tratando e Limpando ao mesmo tempo     * 	@param Var String - Variavel a ser manipulada     * 	@return String     */    public function limpaConverte($var)    {        return $this->converteHTML($this->limpa($var));    }    /**     * 	Passa determinada função a todos o elementos de um Array()     * 	@param Var String - Variavel a ser manipulada     * 	@return String     */    public function passaFuncao($Arr, $Funcao)    {        return array_map($Funcao, $Arr);    }    /**     * 	Retorna a Data e Hora Atual do Servidor     * 	@return String     */    public function dataHora()    {        return date("Y/m/d H:i:s");    }    /**     * 	Retorna a Data Atual do Servidor     * 	@return String     */    public function dataAtual()    {        return date("Y/m/d");    }    public function buscaDataSeparador($Data)    {        if ($Data{2} == "/")            return "/";        if ($Data{5} == "/")            return "/";        if ($Data{2} == "-")            return "-";        if ($Data{5} == "-")            return "-";        if ($Data{4} == "-")            return "-";        return "/";    }    public function nullReturn($valor, $Aspas)    {        if ($Aspas == false) {            return ($valor == "") ? "NULL" : $valor;        } else {            return ($valor == "") ? "NULL" : "'" . $valor . "'";        }    }    /**     * 	Verifica o tamnho de uma string em caracteres     * 	@param String String - String a Ser manipulada     * 	@return Inteiro     */    public function tamanho($String)    {        return strlen($String);    }}